import Axios from "axios";
import { PRODUCT_DETAILS_FAIL, PRODUCT_DETAILS_SUCCESS, PRODUCT_LIST_FAIL, PRODUCT_LIST_REQUEST, PRODUCT_LIST_SUCCESS } from "../constant/productConstant"

export const listProducts = () => async (dispatch) =>{
    dispatch({
        type: PRODUCT_LIST_REQUEST
    });
    try{
        const {data} = await Axios.get('/api/products');
        dispatch({type: PRODUCT_LIST_SUCCESS, payload: data});
    } catch(error){
        dispatch({type: PRODUCT_LIST_FAIL, payload: error.message });
    }
};

export const detailsProduct = (productId) => async(dispatch) =>{
    dispatch({type: PRODUCT_DETAILS_FAIL, payload: productId });
    try{
        const {data} = await Axios.get(`/api/products/${productId}`);
        dispatch({type: PRODUCT_DETAILS_SUCCESS, payload: data});
    } catch{(error)
        dispatch({type: PRODUCT_DETAILS_FAIL, payload: error.respsonse && error.respsonse.data.message? error.respsonse.data.message : error.message, });
    }
};
